import axios from "axios";


export function loadScoreData(input){
    return (dispatch)=> {
        return axios.get("http://localhost:3001/users/"+this.props.id).then((response) => {

            //'http://localhost:8080/widgets'
            dispatch(changeScoreData(response.data))

        })
    }
}

export function changeScoreData(scoreData){
    return {
        type: "CHANGE_SCOREDATA",
        data:scoreData
    }
}
